import React, { Fragment, useState } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Navgation from "./component/Navgation/Navgation";
import Container from "react-bootstrap/Container";
import Home from "./page/Home";

function App() {
  const [films, setfilms] = useState([]);
  const [IsActive, setIsActive] = useState("");

  const handleClick = () => {
    setIsActive(!IsActive);
    console.log("ziad");
  };

  const url = "http://www.omdbapi.com/?apikey=[yourkey]&";
  const options = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "7520e1984amshcb126d4ad4d3e5ap1846acjsnf8edf064be9d",
      "X-RapidAPI-Host": "imdb-top-100-movies.p.rapidapi.com",
    },
  };

  async function fetchmoive() {
    const response = await fetch(url, options);
    const result = await response.json();
    console.log(result);
    // const realdata = result.ser.map((item)=> {
    //   return {
    //     name: item.title,
    //   };
    // });
    // setfilms(realdata);
    const filmss = result.result.map((item) => {
      return {
        name: item.title
      };
    });
    setfilms(filmss);
  }
  return (
    <Fragment>
      <Navgation isActive={IsActive} handleClick={handleClick} />
      <Home mymoive={films} />
      <button onClick={fetchmoive}>show</button>
    </Fragment>
  );
}

export default App;
