import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

const Moivedetails = () => {
    useEffect(() => {
        details();
      }, []);
    
  const [film, setfilm] = useState([]);
  const par = useParams();
  console.log(par.id);
  async function details() {
    const response = await fetch(`https://api.themoviedb.org/3/movie/${par.id}?api_key=5912fc0e7af3d8f1d433c6a241f9e306language=en-US`
       
    );
    const data = await response.json();

    // console.log(data);

    // const datatran = data.results.map((item) => {
    //   return {
    //     id: item.id,
    //     name: item.original_title,
    //     image: item.poster_path,
    //     rate: item.vote_average,
    //   };
    // });
    setfilm(data);
  }
 
  return (
    <div className="detailsonemoive">
      <div className="container-fluid">
        <div className="row">
          <div className="col-lg-12">
            {" "}
            <div className="detailsmoive">{film.original_title}</div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Moivedetails;
