  //   //   };
  //   // });
  //   // setfilms(datatran);
  // }

  return (
    <Fragment>
      <Navgation isActive={IsActive} handleClick={handleClick} search={search} />
      <Home ones={films} getpage={getpage} />

      <Footer/>
    </Fragment>
  );
}

export default App;
